import * as type from "redux/types/user";
const initialState = {
  tokens: JSON.parse(localStorage.getItem("doccords_user")) || {},
};

export const user = (state = initialState, action, payload) => {
  switch (action.type) {
    case type.SET_ACCESS_TOKEN:
      return {
        ...state,
        user: {
          accessToken: payload,
        },
      };
    default:
      return state;
  }
};
