import React from "react";
import MaterialTable from "material-table";
import { Box } from "@material-ui/core";
// import Modal from "@material-ui/core/Modal";
// import Button from "@material-ui/core/Button";

import tableIcons from "components/shared/tableIcons";
const useStyles = makeStyles((theme) => ({
  paper: {
    position: "absolute",
    width: 400,
    backgroundColor: theme.palette.background.paper,
    // border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    borderRadius: "2%",
  },
  button_yes: {
    backgroundColor: "#ed5e68",
    color: "#fff",
  },
  button_no: {
    backgroundColor: "#8388a4",
    marginLeft: "1rem",
    color: "#fff",
  },
  button_create: {
    color: "#fff",
    marginBottom: "2rem",
    alignSelf: "start",
  },
}));
const modalStyle = {
  top: "50%",
  left: `50%`,
  transform: `translate(-50%, -50%)`,
};

const AdminArticles = () => {
  return (
    <Box
      style={{
        // width: "40%",
        margin: "3rem 0",
        padding: "0 3rem",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      {/* <Button
        variant="contained"
        color="secondary"
        className={classes.button_create}
        onClick={navigateToCreatePage}
      >
        Create New Topic
      </Button> */}
      <MaterialTable
        style={{ border: "1px solid #999" }}
        icons={tableIcons}
        title="Health topics list"
        columns={columns}
        data={rows}
        options={{
          search: true,
          actionsColumnIndex: -1,
          fixedColumns: {
            left: 1,
            right: 0,
          },
          headerStyle: {
            backgroundColor: "#4bffa5",
            color: "#000",
          },
        }}
        actions={[
          {
            icon: tableIcons.Edit,
            tooltip: "edit topic",
            onClick: (event, rowData) => {
              navigateToEditPage(
                rowData.title,
                rowData.healthTopicId,
                rowData.picture
              );
            },
          },
          {
            icon: tableIcons.Delete,
            tooltip: "delete topic",
            onClick: (event, rowData) => {
              setOpen(true);
              setDeleteHealthTopicId(rowData.healthTopicId);
            },
          },
        ]}
      />
      {/* <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal> */}
    </Box>
  );
};

export default AdminArticles;

