// Import FirebaseAuth and firebase.
import React from "react";
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";
import firebase from "firebase";
import { PROFILES } from "navigation/constants";

// Configure Firebase.
const firebaseConfig = {
  apiKey: "AIzaSyDG_6b7GzDixXpfpEXwqlyY6qeg0vavTJ4",
  authDomain: "doccords-55659.firebaseapp.com",
  projectId: "doccords-55659",
  storageBucket: "doccords-55659.appspot.com",
  messagingSenderId: "352225564028",
  appId: "1:352225564028:web:b762256bf74fb69c4400f9",
  measurementId: "G-VGGZF4XZSZ",
};
firebase.initializeApp(firebaseConfig);

// Configure FirebaseUI.
const uiConfig = {
  callbacks: {
    signInSuccessWithAuthResult: function (authResult, redirectUrl) {
      const user = authResult.user;
      const credential = authResult.credential;
      const isNewUser = authResult.additionalUserInfo.isNewUser;
      const providerId = authResult.additionalUserInfo.providerId;
      const operationType = authResult.operationType;
      // Do something with the returned AuthResult.
      // Return type determines whether we continue the redirect
      // automatically or whether we leave that to developer to handle.
      console.log(user, credential, isNewUser, providerId, operationType);
      return false;
    },
    signInFailure: function (error) {
      // Some unrecoverable error occurred during sign-in.
      // Return a promise when error handling is completed and FirebaseUI
      // will reset, clearing any UI. This commonly occurs for error code
      // 'firebaseui/anonymous-upgrade-merge-conflict' when merge conflict
      // occurs. Check below for more details on this.
      return console.log(error);
    },
  },
  // Popup signin flow rather than redirect flow.
  signInFlow: "popup",
  // Redirect to /signedIn after sign in is successful. Alternatively you can provide a callbacks.signInSuccess function.
  signInSuccessUrl: PROFILES,
  // We will display Google and Facebook as auth providers.
  signInOptions: [
    firebase.auth.GoogleAuthProvider.PROVIDER_ID,
    {
      provider: firebase.auth.PhoneAuthProvider.PROVIDER_ID,
      recaptchaParameters: {
        type: "image", // 'audio'
        size: "invisible", // 'invisible' or 'compact'
        badge: "bottomleft", // 'bottomright' or 'inline' applies to invisible.
      },

      defaultCountry: "IN",
    },
  ],
};

const LoginView = () => (
  <div>
    <h1>Doccords</h1>
    <p>Login or Signup</p>
    <StyledFirebaseAuth uiConfig={uiConfig} firebaseAuth={firebase.auth()} />
  </div>
);

export default LoginView;
