/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { connect, useSelector } from "react-redux";
import { Paper, makeStyles, Grid, Typography } from "@material-ui/core";
import Controls from "../../../components/Form/controls/Controls";
import { Form } from "../../../components/Form/useForm";
import { fetchAllHealthTopics } from "apiRequests/common";
import { createNewProfile } from "apiRequests/user";
import { useHistory } from "react-router-dom";
import Autocomplete from "@material-ui/lab/Autocomplete";
import TextField from "@material-ui/core/TextField";
import UploadAvatar from "components/shared/upload/uploadAvatar";

const genderItems = [
  { id: "male", title: "Male" },
  { id: "female", title: "Female" },
  { id: "other", title: "Other" },
];
const healthIssuesList = [
  { id: 1, name: "Cancer", status: false },
  { id: 2, name: "Diabetes", status: false },
  { id: 3, name: "Blood pressure", status: false },
  { id: 4, name: "Covid", status: true },
];

const getRelationshipCollection = () => [
  { id: 1, title: "Self" },
  { id: 2, title: "Spouse" },
  { id: 3, title: "Father" },
  { id: 4, title: "Mother" },
  { id: 5, title: "Brother" },
  { id: 6, title: "Sister" },
  { id: 7, title: "Child" },
  { id: 8, title: "Others" },
];

const initialFValues = {
  id: 0,
  profileName: "",
  email: "",
  mobile: "",
  age: "",
  gender: "male",
  relationship: 1,
  // creationDate: new Date(),
  cancer: false,
  bloodPressure: false,
  sugar: false,
  covid: false,
};

const useStyles = makeStyles((theme) => ({
  pageContent: {
    marginTop: "3rem",
    margin: theme.spacing(3),
    padding: theme.spacing(3),
  },
}));

const CreateProfile = ({
  fetchAllHealthTopics,
  healthTopics,
  createNewProfile,
}) => {
  const [documentData, setDocumentData] = useState({
    name: "",
    healthTopicId: "",
    link: "",
  });
  const [healthTopicId, setHealthTopicId] = useState({});
  const [profileName, setProfileName] = useState("");
  const [relationshipId, setRelationshipId] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");
  const [healthIssues, setHealthIssues] = useState(healthIssuesList);
  const [errors, setErrors] = useState({});
  const uploadedLink = useSelector((state) => state.common.uploadedLink);
  const [healthTopicsList, setHealthTopicsList] = useState([]);
  const [knownIssues, setKnownIssues] = useState([]);
  const classes = useStyles();
  const history = useHistory();

  const generateHealthTopics = () => {
    const healthTopicsArr = healthTopics.map((topic) => ({
      id: topic.healthTopicId,
      title: topic.title,
    }));
    return healthTopicsArr;
  };
  useEffect(() => {
    fetchAllHealthTopics();
    setGender(genderItems[0].id);
    setRelationshipId(getRelationshipCollection()[0].id);
  }, []);
  useEffect(() => {
    if (healthTopics.length > 0) {
      setHealthTopicId(healthTopics[0].healthTopicId);
      setHealthTopicsList(generateHealthTopics());
    }
  }, [healthTopics]);

  useEffect(() => {
    setDocumentData({
      ...documentData,
      link: uploadedLink,
    });
  }, [uploadedLink]);
  const onSubmit = () => {
    createNewProfile(
      {
        document: documentData,
        relationshipId,
        profileName,
        age,
        email,
        phone,
        gender,
        healthIssues,
      },
      history
    );
  };

  const handleInputChange = (e) => {
    if (e.target.name === "document") {
      setDocumentData({
        ...documentData,
        name: e.target.value,
      });
    }
    if (e.target.name === "topic") {
      setHealthTopicId(e.target.value);
      setDocumentData({
        ...documentData,
        healthTopicId: e.target.value,
      });
    }
    if (e.target.name === "relationship") {
      setRelationshipId(e.target.value);
    }
    if (e.target.name === "profileName") {
      setProfileName(e.target.value);
    }
    if (e.target.name === "email") {
      setEmail(e.target.value);
    }
    if (e.target.name === "phone") {
      setPhone(e.target.value);
    }
    if (e.target.name === "age") {
      setAge(e.target.value);
    }
    if (e.target.name === "gender") {
      setGender(e.target.value);
    }
  };
  const handleCheckbox = (e, issue) => {
    let changedArray = [];
    if (e.target.name === "healthIssues") {
      changedArray = healthIssues.map((issueSingle) => {
        if (issue.id === issueSingle.id) {
          issueSingle.status = e.target.checked;
        }
        return issueSingle;
      });
    }
    setHealthIssues(changedArray);
  };

  return (
    <>
      <Paper className={classes.pageContent}>
        <Grid container>
          <Grid item xs={12} md={12} lg={12} xl={12}>
            <UploadAvatar />
          </Grid>
        </Grid>
      </Paper>

      <Paper className={classes.pageContent}>
        <Form>
          <Grid container>
            <Grid item xs={12} md={6}>
              <Controls.Input
                name="profileName"
                label="Profile name *"
                value={profileName}
                onChange={handleInputChange}
                error={errors.profileName}
              />
              <Controls.Select
                name="relationship"
                label="Relationship"
                value={relationshipId}
                onChange={handleInputChange}
                options={getRelationshipCollection()}
              />
              <Controls.Input
                label="Email"
                name="email"
                value={email}
                onChange={handleInputChange}
              />
              <Controls.Input
                label="Phone"
                name="phone"
                value={phone}
                onChange={handleInputChange}
              />
              <Controls.Input
                name="age"
                label="Age"
                value={age}
                onChange={handleInputChange}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Controls.RadioGroup
                name="gender"
                label="Gender"
                value={gender}
                onChange={handleInputChange}
                items={genderItems}
              />
              <div className={classes.root}>
                <Typography variant="subtitle1">
                  Any known health issue
                </Typography>
                <Autocomplete
                  multiple
                  limitTags={2}
                  id="multiple-limit-tags"
                  options={healthTopicsList}
                  getOptionLabel={(option) => option.title}
                  defaultValue={knownIssues}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      variant="outlined"
                      label="Health issues"
                      placeholder="Health issues"
                    />
                  )}
                  onChange={(event, value) => {
                    setKnownIssues(value);
                  }}
                />
              </div>

              <Controls.Button
                style={{ display: "block", margin: "0 auto" }}
                onClick={onSubmit}
                text="Submit"
              />
            </Grid>
          </Grid>
        </Form>
      </Paper>
    </>
  );
};

CreateProfile.propTypes = {
  fetchAllHealthTopics: PropTypes.func,
  createNewProfile: PropTypes.func,
  healthTopics: PropTypes.array,
};

const mapStateToProps = (state) => ({
  healthTopics: state.common.healthTopics,
});
const mapDispatchToProps = (dispatch) => ({
  fetchAllHealthTopics: () => dispatch(fetchAllHealthTopics()),
  createNewProfile: (data, history) =>
    dispatch(createNewProfile(data, history)),
});
export default connect(mapStateToProps, mapDispatchToProps)(CreateProfile);

