/* eslint-disable react/prop-types */
/* eslint-disable react/display-name */
import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import { Grid, Avatar } from "@material-ui/core";
import {
  fetchAllProfilesAndDocuments,
  deleteAllProfilesAndDocuments,
} from "apiRequests/user";
import { connect } from "react-redux";
import { useHistory } from "react-router-dom";

const relationship = [
  { id: 1, title: "Self" },
  { id: 2, title: "Spouse" },
  { id: 3, title: "Father" },
  { id: 4, title: "Mother" },
  { id: 5, title: "Brother" },
  { id: 6, title: "Sister" },
  { id: 7, title: "Child" },
  { id: 8, title: "Others" },
];
const useStyles = makeStyles((theme) => ({
  paper: {
    position: "absolute",
    width: 400,
    backgroundColor: theme.palette.background.paper,
    // border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    borderRadius: "2%",
  },
  button_yes: {
    backgroundColor: "#ed5e68",
    color: "#fff",
  },
  button_no: {
    backgroundColor: "#8388a4",
    marginLeft: "1rem",
    color: "#fff",
  },
  button_create: {
    color: "#fff",
    marginBottom: "2rem",
    alignSelf: "start",
  },
}));
const modalStyle = {
  top: "50%",
  left: `50%`,
  transform: `translate(-50%, -50%)`,
};
const Table = (props) => {
  // eslint-disable-next-line react/prop-types
  const {
    add,
    edit,
    fetchAllProfilesAndDocuments,
    profiles,
    documents,
    deleteAllProfilesAndDocuments,
  } = props;
  const history = useHistory();
  const [rows, setRows] = useState([]);
  const [isMultipleSelection, setIsMultipleSelection] = React.useState(false);

  useEffect(() => {
    fetchAllProfilesAndDocuments();
  }, []);

  useEffect(() => {
    setRows(modifyProfiles());
  }, [documents, profiles]);

  const modifyProfiles = () => {
    const modified = profiles.map((profile) => {
      const filteredDocuments = documents.filter(
        (document) => document.profileId === profile.profileId
      );
      profile.uploadedDoc = filteredDocuments.length;
      let shared = 0;
      filteredDocuments.map((filtered) => {
        shared = shared + filtered.sharedList.length;
        return filtered;
      });
      return {
        name: profile.profileName,
        relationshipId: relationship[profile.relationshipId - 1].title,
        uploadedDoc: profile.uploadedDoc,
        sharedDoc: shared,
        profileId: profile.profileId,
      };
    });
    return modified;
  };

  const columns = [
    {
      title: "Name",
      field: "name",
      render: (rowData) => (
        <Grid container alignItems="center">
          <Grid item md={4} xs={6}>
            <Avatar>{rowData.name[0]}</Avatar>
          </Grid>
          <Grid item xs={6} md={8}>
            {rowData.name}
          </Grid>
        </Grid>
      ),
    },
    {
      title: "Relationship",
      field: "relationshipId",
    },
    {
      title: "Uploaded Doc",
      field: "uploadedDoc",
    },
    {
      title: "Shared Doc",
      field: "sharedDoc",
    },
  ];

  const deleteProfiles = (rowData) => {
    const profileIds = rowData.map((row) => row.profileId);
    const docIds = profileIds
      .map((id) => {
        const filteredArray = documents.filter((doc) => id === doc.profileId);
        return filteredArray;
      })
      .flat(1)
      .map((doc) => doc.documentId);
    deleteAllProfilesAndDocuments(profileIds, docIds, history);
  };

  // const handlADDClick () => {

  // }
  // const handlDeleteClick () => {

  // }
  const handlEditClick = (event, rowsData) => {
    console.log(event.target.value);
    if (rowsData.length > 1) {
      return alert("You can't edit more than one");
    }

    // Handle Edit click call back
    edit();
  };

  // const handlNameClick () => {

  // }
  const body = (
    <div style={modalStyle} className={classes.paper}>
      <p id="simple-modal-title">
        Are you sure, you want to delete this health topic?
      </p>
      <div>
        <Button
          variant="contained"
          className={classes.button_yes}
          onClick={clickedYes}
        >
          Yes
        </Button>
        <Button
          variant="contained"
          className={classes.button_no}
          onClick={handleClose}
        >
          No
        </Button>
      </div>
    </div>
  );
  return (
    <div>
      <MaterialTable
        title="Profiles Table"
        data={rows}
        onSelectionChange={(rows) => {
          rows.length > 1
            ? setIsMultipleSelection(true)
            : setIsMultipleSelection(false);
          console.log(rows.length);
        }}
        columns={columns}
        // editable={
        //   onRowUpdate:()=>(newData)=>null,
        // }
        options={{
          search: false,
          paging: false,
          filtering: true,
          // exportButton: true,
          selection: true,
        }}
        actions={[
          {
            icon: "share",
            tooltip: "Share Profile",
            onClick: (event, rowData) => alert("You saved "),
          },
          {
            icon: "edit",
            tooltip: isMultipleSelection
              ? "You can edit only one profile"
              : "Edit Profile",
            onClick: (event, rowsData) => handlEditClick(event, rowsData),
            disabled: isMultipleSelection,
          },
          {
            icon: "add",
            tooltip: "Add Profile",
            isFreeAction: true,
            onClick: (event) => add(),
          },
          {
            icon: "delete",
            tooltip: "Delete User",
            onClick: (event, rowData) => {
              deleteProfiles(rowData);
            },
          },
        ]}
      />
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
};

const mapStateToProps = (state) => ({
  profiles: state.user.profiles,
  documents: state.user.documents,
});
const mapDispatchToProps = (dispatch) => ({
  fetchAllProfilesAndDocuments: () => dispatch(fetchAllProfilesAndDocuments()),
  deleteAllProfilesAndDocuments: (profiles, documents, history) =>
    dispatch(deleteAllProfilesAndDocuments(profiles, documents, history)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Table);
