import React, { useState } from "react";
import firebase from "firebase";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Hidden from "@material-ui/core/Hidden";
import { useAuth } from "context/AuthContext";
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";
import { PROFILES } from "navigation/constants";
import LoginImage from "assets/images/login.png";
// Configure FirebaseUI.
const uiConfig = {
  callbacks: {
    signInFailure: function (error) {
      return console.log(error);
    },
  },
  signInFlow: "popup",
  signInSuccessUrl: PROFILES,
  signInOptions: [
    {
      provider: firebase.auth.GoogleAuthProvider.PROVIDER_ID,
      fullLabel: "Login or Register with Google",
    },
    {
      provider: firebase.auth.FacebookAuthProvider.PROVIDER_ID,
      fullLabel: "Login or Register with Facebook",
    },
    {
      provider: firebase.auth.PhoneAuthProvider.PROVIDER_ID,
      recaptchaParameters: {
        type: "image", // 'audio'
        size: "invisible", // 'invisible' or 'compact'
        badge: "bottomleft", // 'bottomright' or 'inline' applies to invisible.
      },
      fullLabel: "Login or Register with Phone",

      defaultCountry: "IN",
    },
  ],
};
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
}));
const LoginView = () => {
  const classes = useStyles();
  const { currentUser, logout, fetchToken } = useAuth();
  const [tokenView, setTokenView] = useState("Token");
  const tokenClick = async () => {
    const idToken = await fetchToken();
    console.log(idToken);
    setTokenView(idToken);
  };
  const loginDataRender = () => {
    if (currentUser) {
      return (
        <div>
          <p>Logged in</p>
          <button onClick={logout}>signout</button>
          <button onClick={tokenClick}>{tokenView}</button>
        </div>
      );
    }
    return (
      <StyledFirebaseAuth uiConfig={uiConfig} firebaseAuth={firebase.auth()} />
    );
  };

  return (
    <div className={classes.root}>
      <Grid
        container
        spacing={3}
        alignItems="center"
        justify="center"
        style={{ padding: "1rem" }}
      >
        <Hidden xsDown>
          <Grid
            item
            lg={6}
            xl={6}
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <img src={LoginImage} style={{ width: "75%" }} />
          </Grid>
        </Hidden>

        <Grid item xs={24} sm={24} md={6} lg={6} xl={6} height="100">
          {loginDataRender()}
        </Grid>
      </Grid>
    </div>
  );
};

export default LoginView;
