import React from "react";
import { Typography, Button, TextField } from "@material-ui/core";
import { setUploadedImageURL } from "redux/actions/common";
import { useDispatch, useSelector, connect } from "react-redux";

const AdminArticle = () => {
  const dispatch = useDispatch();
  const uploadedLink = useSelector((state) => state.common.uploadedLink);

  const renderUpload = () => {
    if (!uploadedLink.length > 0) {
      return (
        <div className="photo_upload">
          <Typography variant="h5" style={{ marginTop: "1rem" }}>
            Create new health topics
          </Typography>
          <main style={{ marginTop: "1rem" }}>
            <form onSubmit={(e) => handleFileUpload(e)}>
              <label className="uploader">
                <div className="upload-space">
                  {isLoading ? (
                    <Spinner />
                  ) : (
                    <>
                      {isError || isSuccess ? (
                        <i
                          className={`icon-${isSuccess ? "success" : "error"}`}
                        ></i>
                      ) : (
                        <>
                          {preview ? (
                            <div className="preview">
                              <img
                                src={preview}
                                alt="Preview of the file to be uploaded"
                              />
                            </div>
                          ) : (
                            <i className="icon-upload"></i>
                          )}
                          <input
                            type="file"
                            onChange={onFileSelected}
                            accept="image/*"
                          />
                        </>
                      )}
                    </>
                  )}
                </div>
                {isError || isSuccess ? (
                  <p className={isSuccess ? "success" : "error"}>
                    {isSuccess
                      ? "Upload successful!"
                      : "Something went wrong ..."}
                  </p>
                ) : (
                  <p className="filename">
                    {fileName || "No file selected yet"}
                  </p>
                )}
              </label>

              <Button
                type="submit"
                variant="contained"
                color="primary"
                disabled={isDisabled}
                style={{
                  textTransform: "none",
                  marginTop: "1rem",
                }}
              >
                {buttonText}
              </Button>
            </form>
          </main>
        </div>
      );
    } else {
      return (
        <div style={{ display: "flex", flexDirection: "column" }}>
          <img
            src={uploadedLink}
            style={{ maxWidth: "15rem", height: "auto" }}
          />
          <Button
            variant="contained"
            color="secondary"
            style={{
              color: "#ffffff",
              textTransform: "none",
              width: "fit-content",
              marginTop: "1rem",
            }}
            onClick={deleteUploadedUrl}
            width="auto"
          >
            Delete uploaded image
          </Button>
        </div>
      );
    }
  };

  return (
    <div>
      <Button
        variant="contained"
        color="secondary"
        style={{
          color: "#ffffff",
          textTransform: "none",
          width: "fit-content",
          margin: "1rem 0",
        }}
        onClick={() => {
          history.goBack();
          dispatch(setUploadedImageURL(""));
        }}
      >
        Go back
      </Button>
      {renderUpload()}
      <div style={{ marginTop: "2rem" }}>
        <form className={classes.root} autoComplete="off">
          <TextField
            error={error.title.status}
            id="title"
            label="Please enter title here"
            variant="outlined"
            width="10rem"
            size="medium"
            helperText={error.title.text}
            onChange={handleChange}
            value={title}
          />
          {!healthTopicId && (
            <Button
              disabled={!uploadedLink}
              variant="contained"
              color="primary"
              style={{
                textTransform: "none",
                width: "fit-content",
                marginTop: "2rem",
                display: "block",
              }}
              onClick={onCreate}
            >
              Create
            </Button>
          )}
          {healthTopicId && (
            <Button
              disabled={!uploadedLink}
              variant="contained"
              color="primary"
              style={{
                textTransform: "none",
                width: "fit-content",
                marginTop: "2rem",
                display: "block",
              }}
              onClick={onUpdate}
            >
              Update
            </Button>
          )}
        </form>
      </div>
    </div>
  );
};

export default AdminArticle;
