//     marginBottom: "2rem",
//     alignSelf: "start",
//   },
// }));
// const modalStyle = {
//   top: "50%",
//   left: `50%`,
//   transform: `translate(-50%, -50%)`,
// };

const AdminArticles = ({ articles }) => {
  // const classes = useStyles();
  const [rows, setRows] = useState([]);
  const [columns, setColumns] = useState([]);

  useEffect(() => {
    fetchAllArticles();
  }, []);
  useEffect(() => {
    setColumns(getColumnData());
    setRows(articles);
  }, [articles]);

  const getColumnData = () => [
    { field: "title", title: "Title", width: 300 },
    { field: "description", title: "Description", width: 300 },
    { field: "healthTopicTitle", Title: "Health Topic" },
    {
      field: "picture",
      title: "Picture",
      sorting: false,
      render: function renderImage(rowData) {
        return (
          <img
            src={rowData.picture}
            style={{ maxWidth: "50%", height: "auto" }}
          />
        );
      },
    },
  ];

  return (
    <Box
      style={{
        // width: "40%",
        margin: "3rem 0",
        padding: "0 3rem",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      {/* <Button
        variant="contained"
        color="secondary"
        className={classes.button_create}
        onClick={navigateToCreatePage}
      >
        Create New Topic
      </Button> */}
      <MaterialTable
        style={{ border: "1px solid #999" }}
        icons={tableIcons}
        title="Health topics list"
        columns={columns}
        data={rows}
        options={{
          search: true,
          actionsColumnIndex: -1,
          fixedColumns: {
            left: 1,
            right: 0,
          },
          headerStyle: {
            backgroundColor: "#4bffa5",
            color: "#000",
          },
        }}
        actions={[
          {
            icon: tableIcons.Edit,
            tooltip: "edit topic",
            onClick: (event, rowData) => {
              navigateToEditPage(
                rowData.title,
                rowData.healthTopicId,
                rowData.picture
              );
            },
          },
          {
            icon: tableIcons.Delete,
            tooltip: "delete topic",
            onClick: (event, rowData) => {
              setOpen(true);
              setDeleteHealthTopicId(rowData.healthTopicId);
            },
          },
        ]}
      />
      {/* <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal> */}
    </Box>
  );
};

export default AdminArticles;

