import React, { useState } from "react";
// Router
import { BrowserRouter } from "react-router-dom";
import { RouterConfig } from "navigation/RouterConfig";
// MUI Theme
import { ThemeProvider } from "@material-ui/core";
import Box from "@material-ui/core/Box";
import { ThemeSwitch } from "components/ThemeSwitch";
import { dark, light } from "styles/muiTheme";
// Redux
import { Provider } from "react-redux";
import { store } from "redux/store";

import TopNavbar from "./components/Appbar/topNavbar";
// import Navbar from "./components/Navbar/Navbar";
import BottomTabbar from "./components/Appbar/bottomTabbar";

// styles
import "./App.css";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  sectonMDandUP: {
    display: "none",
    [theme.breakpoints.down("sm")]: {
      display: "block",
    },
  },
}));

function App() {
  const classes = useStyles();

  const [darkState, setDarkState] = useState(false);
  const handleThemeChange = () => {
    setDarkState(!darkState);
    console.log("theme=", darkState ? "dark" : "light");
  };
  return (
    <>
      <div>
        <Provider store={store}>
          <ThemeProvider theme={darkState ? dark() : light()}>
            <ThemeSwitch
              darkState={darkState}
              handleThemeChange={handleThemeChange}
            />
            <BrowserRouter>
              <TopNavbar />
              {/* <Navbar /> */}
              <RouterConfig />
              <Box className={classes.sectonMDandUP}>
                <BottomTabbar className={classes.sectonMDandUP} />
              </Box>
            </BrowserRouter>
          </ThemeProvider>
        </Provider>
      </div>
    </>
  );
}

export default App;
