import axios from "axios";
import { setAccessToken, setUserDetial } from "redux/actions/user";
import { fetchTokenFromServer } from "context/AuthContext";

export const fetchUserDetail = async (dispatch) => {
  const idToken = await fetchTokenFromServer();
  dispatch(setAccessToken(idToken));
  const userDetail = await axios.get("http://localhost:5001/api/v1/users", {
    headers: {
      Authorization: "Bearer " + idToken,
    },
  });
  dispatch(setUserDetial(userDetail.data.user));
  dispatch(changeLoading);
};
