// Import FirebaseAuth and firebase.
import React, { useState, useEffect } from "react";
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";
import firebase from "firebase";
import { SETUP_PROFILE } from "navigation/constants";

// Configure FirebaseUI.
const uiConfig = {
  callbacks: {
    signInSuccessWithAuthResult: function (authResult, redirectUrl) {
      firebase
        .auth()
        .currentUser.getIdToken(true)
        .then(function (idToken) {
          // Send token to your backend via HTTPS
          // ...
          console.log("tokennnnnnnnnn", idToken);
        })
        .catch(function (error) {
          console.error(error);
        });
      // localStorage.setItem("doccords_user", JSON.stringify(user));
      // console.log(authResult);
      return false;
    },
    signInFailure: function (error) {
      return console.log(error);
    },
  },
  signInFlow: "popup",
  signInSuccessUrl: SETUP_PROFILE,
  signInOptions: [
    {
      provider: firebase.auth.GoogleAuthProvider.PROVIDER_ID,
      fullLabel: "Login or Register with Google",
    },
    {
      provider: firebase.auth.FacebookAuthProvider.PROVIDER_ID,
      fullLabel: "Login or Register with Facebook",
    },
    {
      provider: firebase.auth.PhoneAuthProvider.PROVIDER_ID,
      recaptchaParameters: {
        type: "image", // 'audio'
        size: "invisible", // 'invisible' or 'compact'
        badge: "bottomleft", // 'bottomright' or 'inline' applies to invisible.
      },
      fullLabel: "Login or Register with Phone",

      defaultCountry: "IN",
    },
  ],
};

const LoginView = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged((userParams) => {
      // detaching the listener
      if (userParams) {
        // ...your code to handle authenticated users.
        setUser(userParams);
      } else {
        // No user is signed in...code to handle unauthenticated users.
      }
    });
    return () => unsubscribe(); // unsubscribing from the listener when the component is unmounting.
  }, []);

  console.log("user", user);
  if (user) {
  } else {
    return (
      <div>
        <h1>Doccords</h1>
        <p>Login or Signup</p>
        <StyledFirebaseAuth
          uiConfig={uiConfig}
          firebaseAuth={firebase.auth()}
        />
      </div>
    );
  }
};

export default LoginView;
