/* eslint-disable react/prop-types */
import React, { useState, useEffect } from "react";
import Drawer from "@material-ui/core/Drawer";
import withStyles from "@material-ui/core/styles/withStyles";
import { useAuth } from "context/AuthContext";
import {
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
} from "@material-ui/core";
import InboxIcon from "@material-ui/icons/MoveToInbox";
import MailIcon from "@material-ui/icons/Mail";
import { useSelector } from "react-redux";
import { ROOT, HEALTH_TOPICS, ARTICLES } from "navigation/constants";

const styles = () => ({
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
});
const navList = {
  // registeredUser: [
  //   "Home",
  //   "Dashboard",
  //   "Health Topics",
  //   "Articles",
  //   "Profiles",
  //   "Documents",
  // ],
  // admin: ["Dashboard", "Health Topics", "Articles"],
  unRegisteredUser: [
    {
      name: "Home",
      link: ROOT,
    },
    {
      name: "Health Topics",
      link: HEALTH_TOPICS,
    },
    {
      name: "Articles",
      link: ARTICLES,
    },
  ],
};

const DrawerComponent = ({ classes, toggleDrawerHandler, left }) => {
  const { currentUser } = useAuth();
  const userDetail = useSelector((state) => state.user.userDetail);
  const [list, setList] = useState(["Home", "Health Topics", "Artiles"]);
  // const [left] = useState(false);
  useEffect(() => {
    if (currentUser) {
      if (userDetail.profileType === "admin") {
        setList(navList.admin);
      } else if (userDetail.profileType === "free") {
        setList(navList.registeredUser);
      }
    } else {
      setList(navList.unRegisteredUser);
    }
  }, [userDetail]);

  const sideList = (list) => (
    <div
      className={classes.list}
      role="presentation"
      onClick={toggleDrawerHandler}
      onKeyDown={toggleDrawerHandler}
    >
      <List>
        {list.map((item, index) => (
          <ListItem href={item.link}>
            <ListItemIcon>
              {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      {/* <List>
        {["All mail", "Trash", "Spam"].map((text, index) => (
          <ListItem button key={text}>
            <ListItemIcon>
              {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List> */}
    </div>
  );

  return (
    <Drawer open={left} onClose={toggleDrawerHandler}>
      {sideList(list)}
    </Drawer>
  );
};

export default withStyles(styles)(DrawerComponent);
