/* eslint-disable react/prop-types */
// A wrapper for <Route> that redirects to the login
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, Route } from "react-router-dom";
import { useAuth } from "context/AuthContext";
import { fetchUserDetail } from "apiRequests/user";
import { ADMIN_DASHBOARD } from "navigation/constants";

// screen if you're not yet authenticated.
const PrivateRoute = ({ children, ...rest }) => {
  const userDetail = useSelector((state) => state.user.userDetail);
  const loading = useSelector((state) => state.user.loading);
  const { currentUser } = useAuth();
  const dispatch = useDispatch();

  useEffect(() => {
    if (currentUser) {
      console.log("came here");
      fetchUserDetail(dispatch);
    }
  }, []);
  console.log(currentUser, userDetail);
  if (currentUser) {
    if (loading) {
      return <p>Loading</p>;
    }
    if (userDetail.profileType === "free") {
      return <Route {...rest} render={() => children} />;
    } else if (userDetail.profileType === "admin") {
      console.log("Does it came here");
      return (
        <Route
          {...rest}
          render={() => <Redirect to={{ pathname: ADMIN_DASHBOARD }} />}
        />
      );
    }
  } else {
    return (
      <Route
        {...rest}
        render={() => <Redirect to={{ pathname: "/login" }} />}
      />
    );
  }
};
export default PrivateRoute;
