import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Redirect, Route } from "react-router-dom";
import { useAuth } from "context/AuthContext";
import { fetchUserDetail } from "apiRequests/user";

// screen if you're not yet authenticated.
const AdminPrivateRoute = ({ children, ...rest }) => {
  const userDetail = useSelector((state) => state.user.userDetail);
  const { currentUser } = useAuth();
  const dispatch = useDispatch();

  useEffect(() => {
    if (currentUser) {
      console.log("came here");
      fetchUserDetail(dispatch);
    } else {
      <Redirect to={{ pathname: "/login" }} />;
    }
  }, []);

  if (currentUser && userDetail.profileType === "admin") {
    return <Route {...rest} render={() => children} />;
  } else if (currentUser && userDetail.profileType === "free") {
    return (
      <Route
        {...rest}
        render={() => <Redirect to={{ pathname: "/unauthorized" }} />}
      />
    );
  } else {
    return (
      <Route
        {...rest}
        render={() => <Redirect to={{ pathname: "/login" }} />}
      />
    );
  }
};
export default AdminPrivateRoute;
